import {useState} from "react";
import './App.css';
import { quizData } from "./quizData";
import html2canvas from "html2canvas";
import jsPDF from "jspdf";

//bootstrap
import 'bootstrap/dist/css/bootstrap.min.css'; 
import Button from 'react-bootstrap/Button'; 

function App() {
  //State管理
  const[currentQuestion,setCurrentQuestion] = useState(0);
  const[next,setNext] = useState(false);
  const[answer,setAnswer] = useState([]);
  const[score,setScore] = useState(0);
  const[feedback,setFeedback] = useState(null);
  const[showScore,setShowScore] = useState(false);

  //データをすべて初期化する関数
  const goToFirstQuestion = () => {
    setCurrentQuestion(0);
    setNext(false);
    setAnswer([]);
    setScore(0);
    setFeedback(null);
    setShowScore(false);
  };

  //解答レポートをpdfにする関数
  function downloadPDF() {
    const element = document.getElementById("pdf-content"); // PDF化したい要素のIDを指定
    if (!element) {
      console.error("Element not found");
      return;
    }
    //pdfの様式指定
    html2canvas(element).then((canvas) => {
      const imgData = canvas.toDataURL("image/png"); // キャプチャした要素を画像データとして取得
      const pdf = new jsPDF("p", "mm", "a4");
      // A4サイズに合わせて画像を縮小
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (canvas.height * pdfWidth) / canvas.width;
      pdf.addImage(imgData, "PNG", 0, 0, pdfWidth, pdfHeight);
      pdf.save("download.pdf"); // ファイル名を指定してダウンロード
    });
  }

  //次の質問に移動する関数
  const goToNextQuestion = () => {
    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < quizData.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
    setNext(false);
    setFeedback(null);
  };

  //答え合わせをする関数
  const handleAnswer = (answer) =>{
    const newAnswer = {
        question: quizData[currentQuestion].question,
        answer: answer,
        rightanswer: quizData[currentQuestion].correct,
        correct: quizData[currentQuestion].correct=== answer,
    };
    // correct
    if(newAnswer.correct){
      setScore((prevScore) => prevScore + 1);
      setFeedback("正解です");
    }else{
      setFeedback("不正解です");
    }


    // スプレッド構文。...は空の構文を展開する。newAnswerでオブジェクトを追加する。
    // setAnswer([...answer,newAnswer]);
    setAnswer((prevAnswer) => [...prevAnswer, newAnswer]);
    setNext(true);
  }


  return (
      <div className='quiz-container'>
        {showScore ? (
          <div className="score-section">
            <scope id="pdf-content">  {/* pdfの範囲 */}
              <h1>スコア</h1>
              <h2>{score}/{quizData.length}</h2>
              <table className="answer-table">
                <thead>
                  <tr>
                    <td>質問</td>
                    <td>あなたの解答</td>
                    <td>正解</td>
                    <td>合否</td>
                  </tr>
                </thead>
                <tbody>
                  {answer.map((item) => (
                    <tr className={item.correct ? 'correct' : 'wrong'}>
                      <td>{item.question}</td>
                      <td>{item.answer}</td>
                      <td>{item.rightanswer}</td>
                      <td>{item.correct ? 'OK' : 'NG'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </scope>
            <Button variant="primary" className="my-primary-button" 
            onClick={goToFirstQuestion}>
              最初から
            </Button>
            <Button variant="primary" className="my-primary-button" 
            onClick={downloadPDF}>
              レポート作成
            </Button>
          </div>
        ) : (
      <div className='question-section'>
        <h1>問題 {currentQuestion+1} / {quizData.length}</h1>
        <h2>{quizData[currentQuestion].question}</h2>
        {next ? (
          <div className="feedback-section">
            <h2 className="large-feedback">{feedback}</h2>
            <p>解答</p>
            <p className="answe-data">{quizData[currentQuestion].correct}</p>
            <Button onClick= {goToNextQuestion}>次の問題へ</Button>
          </div>
        ) : (
        <div className="answer-section">
          {quizData[currentQuestion].options.map((item) => (
            <Button
            key={item}        
            onClick={() => handleAnswer(item)} 
            className="quiz-option-button"
            size="lg"
            >
            {item}
            </Button>
          ))}
        </div>
        )}
      </div>
    )}
      </div>
  );
}

export default App;
